(1, 'en_US', 'openAccessPolicy', 'This journal provides immediate open access to its content on the principle that making research freely available to the public supports a greater global exchange of knowledge.', 'string'),
(1, 'en_US', 'pageHeaderTitleImage', 'a:6:{s:4:"name";s:14:"logo_wimba.png";s:10:"uploadName";s:30:"pageHeaderTitleImage_en_US.png";s:5:"width";i:300;s:6:"height";i:80;s:8:"mimeType";s:9:"image/png";s:12:"dateUploaded";s:19:"2015-04-29 06:45:25";}', 'object'),
